$currentDate = date('Y-m-d');

// Get the day of the week (0 for Sunday, 1 for Monday, etc.)
$dayOfWeek = date('w', strtotime($currentDate));

// Calculate the difference between the current day and Monday (0 for Monday, 1 for Tuesday, etc.)
$diffToMonday = $dayOfWeek - 1;

// Calculate the difference between the current day and Friday (0 for Friday, 1 for Thursday, etc.)
$diffToFriday = 5 - $dayOfWeek;

// Calculate the dates for Monday and Friday
$monday = date('Y-m-d', strtotime("-$diffToMonday days", strtotime($currentDate)));
$friday = date('Y-m-d', strtotime("+$diffToFriday days", strtotime($currentDate)));

