class CustomException extends Exception {
    private $extraParam;

    public function __construct($message, $code, $extraParam) {
        parent::__construct($message, $code);
        $this->extraParam = $extraParam;
    }

    public function getExtraParam() {
        return $this->extraParam;
    }
}

// Usage
